:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:795:\"\n<p>When you’re building a community site with BuddyPress or BuddyBoss, the user’s profile page becomes a major hub for where they jump into community conversations and manage their account.</p>\n\n\n\n<p>In the current release of <a href=\"https://www.paidmembershipspro.com/add-ons/buddypress-integration/\">PMPro’s BuddyPress and BuddyBoss integration Add On</a>, we added a feature to extend the default profile with membership information by adding a new membership section.</p>\n\n\n\n<p>In this tutorial, we show you how to use the <code>pmpro_buddypress_profile_account_shortcode</code> filter to adjust the default output of the membership tab based on your needs.</p>\n\n\n\n<div class=\"pmpro_content_message\">This content is for members only. Visit the site and log in/register to read.</div>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:9;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:3:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"How to Migrate from Ultimate Member to Paid Memberships Pro\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:68:\"https://www.paidmembershipspro.com/migrate-ultimate-member-to-pmpro/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 07 Aug 2023 13:30:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:7:\"General\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:28:\"Migrate Your Membership Site\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:44:\"https://www.paidmembershipspro.com/?p=508743\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:462:\"Looking to elevate your membership experience? If you&#8217;ve been using Ultimate Member but crave more advanced features and customization options, it&#8217;s time to migrate from Ultimate Member to Paid Memberships Pro. In this guide we break down the exact steps to migrate your user list in Ultimate Member into a complete membership site powered by <a href=\"https://www.paidmembershipspro.com/migrate-ultimate-member-to-pmpro/\" rel=\"nofollow\">(more...)</a>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:10:\"PMPro Team\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:41556:\"\n<p>Looking to elevate your membership experience? If you&#8217;ve been using Ultimate Member but crave more advanced features and customization options, it&#8217;s time to migrate from Ultimate Member  to Paid Memberships Pro.</p>\n\n\n\n<p>In this guide we break down the exact steps to <strong>migrate your user list in Ultimate Member into a complete membership site powered by Paid Memberships Pro</strong>. This developer-focused guide summarizes everything we’ve learned about the migration process into step-by-step instructions.</p>\n\n\n\n<img loading=\"lazy\" width=\"852\" height=\"479\" src=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrating_from_Ultimate_Member-1024x576.jpg\" class=\"attachment-large size-large wp-post-image\" alt=\"Banner Image for Migrating from Ultimate Member to Paid Memberships Pro\" decoding=\"async\" srcset=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrating_from_Ultimate_Member-1024x576.jpg 1024w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrating_from_Ultimate_Member-300x169.jpg 300w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrating_from_Ultimate_Member-1170x658.jpg 1170w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrating_from_Ultimate_Member-128x72.jpg 128w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrating_from_Ultimate_Member.jpg 1200w\" sizes=\"(max-width: 852px) 100vw, 852px\" />\n\n\n\n<div class=\"wp-block-group has-grey-blue-background-color has-background has-global-padding is-layout-constrained wp-block-group-is-layout-constrained\">\n<div class=\"wp-block-yoast-seo-table-of-contents yoast-table-of-contents\"><h2>Table of contents</h2><ul><li><a href=\"#migrate-to-pmpro\" data-level=\"2\">Make the Decision to Migrate to PMPro</a></li><li><a href=\"#safely-migrate\" data-level=\"2\">How to Safely Migrate a Membership Site: Ultimate Member to Paid Memberships Pro</a></li><li><a href=\"#migrate-members\" data-level=\"2\">Part 1: Migrating Members</a><ul><li><a href=\"#backup-website\" data-level=\"3\">1. Backup Your WordPress Website</a></li><li><a href=\"#pause-live-website\" data-level=\"3\">2. Pause Your Live Website</a></li><li><a href=\"#install-set-up-paid-memberships-pro\" data-level=\"3\">3. Install and Set Up Paid Memberships Pro</a></li><li><a href=\"#set-up-pmpro-user-fields\" data-level=\"3\">4. Set Up User Fields (If Applicable)</a></li><li><a href=\"#export-import\" data-level=\"3\">5. Export and Import Your Data</a></li><li><a href=\"#resume-your-live-website\" data-level=\"3\">6. Resume Your Live Website</a></li></ul></li><li><a href=\"#configure-content-protection-and-features\" data-level=\"2\">Part 2: Configuring Content Protection and Other Features</a><ul><li><a href=\"#restrict-wordpress-content\" data-level=\"3\">Restrict Access to WordPress Content</a></li><li><a href=\"#create-member-directory\" data-level=\"3\">Create a Member Directory and Public or Private Profiles</a></li><li><a href=\"#replicate-features\" data-level=\"3\">Replicate Other Ultimate Member Features</a></li></ul></li><li><a href=\"#migration-complete\" data-level=\"2\">Welcome to The Future: A More Powerful Membership Site</a></li><li><a href=\"#frequently-asked-questions\" data-level=\"2\">Frequently Asked Questions</a></li></ul></div>\n</div>\n\n\n\n<h2 class=\"wp-block-heading\" id=\"migrate-to-pmpro\">Make the Decision to Migrate to PMPro</h2>\n\n\n\n<p>Embracing PMPro will unlock a world of possibilities for growing and efficiently managing your membership site. While switching platforms may sound scary, there is a way to safely migrate from Ultimate Member, while preserving all of your existing user profile data.</p>\n\n\n\n<p>No matter what type of membership site you operate—whether it&#8217;s a course or coaching site, online community, premium subscription platform, or member directory—PMPro offers the most advanced features and deeper customization options, tailored to suit your unique needs.</p>\n\n\n\n<div class=\"wp-block-group is-style-alert-message has-global-padding is-layout-constrained wp-block-group-is-layout-constrained\">\n<p>Note: This guide is specifically written to support someone migrating active members from Ultimate Member to Paid Memberships Pro. Ultimate Member does not have native payments, so we do not cover how to migrate subscriptions in this guide.</p>\n</div>\n\n\n\n<p>If you are using a premium version of Ultimate Member with WooCommerce, refer to our <a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-import-users-csv/#subscriptions\">documentation on maintaining active subscriptions through the import process here</a>.</p>\n\n\n\n<h2 class=\"wp-block-heading\" id=\"safely-migrate\">How to Safely Migrate a Membership Site: Ultimate Member to Paid Memberships Pro</h2>\n\n\n\n<p>Active membership sites are constantly changing—with things like new member signups, cancellations, and upgrades, receiving new post comments or posts in forums, and myriad other engagement activities.</p>\n\n\n\n<p>So when it comes to migrating your membership site to a new platform, it’s important to minimize interruptions to the live environment.</p>\n\n\n\n<p>For this reason, we recommend that you temporarily close your membership site to the public. If your site happens to be on the smaller side, you can probably take it down for a day or two and not suffer any major consequences.</p>\n\n\n\n<p>If you can’t possibly put your site in maintenance mode, we’ve included information on how to complete your migration through the use of a staging environment.</p>\n\n\n\n<p>The process of migrating from Ultimate Member to Paid Memberships Pro involves two crucial steps:</p>\n\n\n\n<ul>\n<li>Transferring your membership data to PMPro.</li>\n\n\n\n<li>Moving the content protections from the old system to the new system.</li>\n</ul>\n\n\n\n<p>This guide will provide you with comprehensive instructions and advice to ensure a smooth migration. By following these steps, you will be able to maintain access for all existing members that joined via Ultimate Member, and begin accepting new member registrations (and new subscription revenue) using Paid Memberships Pro.</p>\n\n\n\n<p>So, let’s get started.</p>\n\n\n\n<div class=\"wp-block-group content-callout has-background is-layout-flow wp-block-group-is-layout-flow\" style=\"background-color:#f5f8fa\">\n<div class=\"wp-block-group is-nowrap is-layout-flex wp-container-121 wp-block-group-is-layout-flex\">\n<p><i class=\"fa  fa-glasses\"></i> <strong>Related:</strong> <a href=\"https://www.paidmembershipspro.com/paid-memberships-pro-vs-ultimate-member/\">Paid Memberships Pro vs. Ultimate Member Comparison Guide</a></p>\n</div>\n</div>\n\n\n\n<h2 class=\"wp-block-heading\" id=\"migrate-members\">Part 1: Migrating Members</h2>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"backup-website\">1. Backup Your WordPress Website</h3>\n\n\n\n<p>In our article <a href=\"https://www.paidmembershipspro.com/set-up-a-data-backup-process-for-your-wordpress-membership-site/\">Backup Your WordPress Site: The Complete Guide to Site Backups</a>, we highlight the significance of frequent backups and provide a guide on how to create one for your site. This is particularly crucial for membership sites that involve customer accounts and sensitive information.</p>\n\n\n\n<p><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f449.png\" alt=\"👉\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> Please make sure to create a backup of your site before proceeding with the migration process.</p>\n\n\n\n<div class=\"wp-block-group alignfull has-grey-blue-background-color has-background has-global-padding is-layout-constrained wp-block-group-is-layout-constrained\" id=\"callout\">\n<div class=\"wp-block-columns alignwide are-vertically-aligned-center is-layout-flex wp-container-125 wp-block-columns-is-layout-flex\">\n<div class=\"wp-block-column is-vertically-aligned-center is-layout-flow wp-block-column-is-layout-flow\" style=\"flex-basis:20%\">\n<figure class=\"wp-block-image size-large\"><img decoding=\"async\" loading=\"lazy\" width=\"791\" height=\"1024\" src=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Ebook-How-to-Migrate-Ultimate-Member-to-Paid-Memberships-Pro-791x1024.jpg\" alt=\"Ebook Cover: How to Migrate to PMPro from Ultimate Member\" class=\"wp-image-508802\" srcset=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Ebook-How-to-Migrate-Ultimate-Member-to-Paid-Memberships-Pro-791x1024.jpg 791w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Ebook-How-to-Migrate-Ultimate-Member-to-Paid-Memberships-Pro-232x300.jpg 232w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Ebook-How-to-Migrate-Ultimate-Member-to-Paid-Memberships-Pro-927x1200.jpg 927w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Ebook-How-to-Migrate-Ultimate-Member-to-Paid-Memberships-Pro.jpg 966w\" sizes=\"(max-width: 791px) 100vw, 791px\" /></figure>\n</div>\n\n\n\n<div class=\"wp-block-column is-vertically-aligned-center is-layout-flow wp-block-column-is-layout-flow\" style=\"flex-basis:80%\">\n<p class=\"has-21-font-size\"><i class=\"fa  fa-file-pdf\"></i> <strong>DOWNLOAD NOW</strong></p>\n\n\n\n<p>We have a downloadable version of this setup guide with the specific steps you need to&nbsp;<strong>convert Ultimate Member to PMPro—without affecting current member profiles</strong>. </p>\n\n\n<script async data-uid=\"d4dbc7b881\" src=\"https://strangerstudios.ck.page/d4dbc7b881/index.js\"></script>\n</div>\n</div>\n</div>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"pause-live-website\">2. Pause Your Live Website</h3>\n\n\n\n<p>A smooth migration from Ultimate Member means that you do not lose or mismatch any data. To ensure this doesn’t happen, we recommend temporarily pausing your live website. This includes:</p>\n\n\n\n<ul>\n<li>Deactivating all new membership sign-ups</li>\n\n\n\n<li>Putting your main website in “Maintenance Mode” or “Coming Soon” mode.</li>\n</ul>\n\n\n\n<p>You can use plugins like <a href=\"https://wordpress.org/plugins/wp-maintenance-mode/\">WP Maintenance Mode &amp; Coming Soon</a> to add a maintenance or coming soon page to your website.</p>\n\n\n\n<p>Note: If it’s not possible to pause your active site, proceed to <a href=\"#prepare-import\">Prepare Your Import Sheet</a>. Keep in mind that some data may still need to be moved before the final import, which is normal.</p>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"install-set-up-paid-memberships-pro\">3. Install and Set Up Paid Memberships Pro</h3>\n\n\n\n<p>It’s time to install Paid Memberships Pro on your website or on <a href=\"https://www.paidmembershipspro.com/documentation/download/staging-or-development-sites/\">a staging site</a>.</p>\n\n\n\n<p>Note: If you’re unable to turn off your main website or want to test PMPro’s functionality, it’s advisable to use a staging site or a <a href=\"https://www.paidmembershipspro.com/how-to-set-up-a-demo-membership-site/\">local development site</a>.</p>\n\n\n\n<p>Keep in mind that once you create a staging site, your live site and development site will no longer be in sync. If you choose this option, there will be some Ultimate Member data that you’ll need to migrate to the new site before it goes live.</p>\n\n\n\n<h4 class=\"wp-block-heading\" id=\"install-on-main-website\">If You’re Installing PMPro on Your Main Website</h4>\n\n\n\n<p>Our <a href=\"https://www.paidmembershipspro.com/documentation/initial-plugin-setup/\">Initial Setup</a> guide provides information on how to install PMPro through the WordPress dashboard or by downloading a PMPro package.</p>\n\n\n\n<p>When you are setting up PMPro, you may instead want to edit and use your existing Ultimate Member pages as the same page PMPro requires. Here’s a table explaining what to do with each Ultimate Member Page and PMPro required page.</p>\n\n\n\n<div class=\"wp-block-group alignfull has-grey-blue-background-color has-background has-global-padding is-layout-constrained wp-block-group-is-layout-constrained\">\n<figure class=\"wp-block-table alignwide\"><table class=\"has-white-background-color has-background has-fixed-layout\"><thead><tr><th><strong>Ultimate Member Page</strong></th><th><strong>PMPro Page</strong></th><th><strong>Recommended Action</strong></th></tr></thead><tbody><tr><td>Register</td><td>Membership Checkout Page</td><td>Edit the “Register” page and replace the UM shortcode with the Membership Checkout shortcode or block.</td></tr><tr><td>Login</td><td>Log In Page</td><td>Edit the “Login” page and replace the UM shortcode with the Log In shortcode or block.</td></tr><tr><td>User</td><td>Profile Page<br><br>Note: This is only used by the <a href=\"https://www.paidmembershipspro.com/add-ons/member-directory/\">Member Directory and Profile Page Add On</a>.</td><td>Edit the “User” page and replace the UM shortcode with the Member Profile shortcode or block</td></tr><tr><td>Account</td><td>Membership Account Page</td><td>Edit the “Account” page and replace the UM shortcode with the Membership Account shortcode or block.</td></tr><tr><td>Members</td><td>Directory Page<br><br>Note: This is only by the <a href=\"https://www.paidmembershipspro.com/add-ons/member-directory/\">Member Directory and Profile Page Add On</a>.</td><td>Edit the “Members” page and replace the UM shortcode with the Member Directory shortcode or block.</td></tr><tr><td>Logout</td><td>Not used. PMPro uses the Log In page for these actions.</td><td>You can safely delete this Ultimate Member page at the end of the process.</td></tr><tr><td>Password Reset</td><td>Not used. PMPro uses the Log In page for these actions</td><td>You can safely delete this Ultimate Member page at the end of the process.</td></tr></tbody></table></figure>\n</div>\n\n\n\n<h4 class=\"wp-block-heading\" id=\"membership-levels-vs-user-roles\">Membership Levels vs. User Roles</h4>\n\n\n\n<p>Before we dive into adding and configuring membership levels, it is important to <a href=\"https://www.paidmembershipspro.com/roles-levels-membership-sites/\">understand the difference between roles and membership levels</a>.</p>\n\n\n\n<p>Ultimate Member <strong>uses WordPress user roles to manage user capabilities</strong>.</p>\n\n\n\n<p>On the other hand, Paid Memberships Pro intentionally separates user capabilities from membership access and instead <strong>uses membership levels to manage members.</strong></p>\n\n\n\n<p>For this migration guide, most sites can simply eliminate any custom roles they had configured via Ultimate Member and instead reset all users to the default role of “Subscriber” during the import process (covered later in this guide). But there are a few exceptions:</p>\n\n\n\n<p><strong>You need to keep using custom roles if:</strong></p>\n\n\n\n<ul>\n<li>Your membership gives users access to do advanced things within the WordPress admin, like editing or publishing posts, moderating forums, approving comments, and more.</li>\n\n\n\n<li>You need to protect access to another WordPress plugin or third party feature that cannot be done using membership levels. Refer to our guide on how to <a href=\"https://www.paidmembershipspro.com/restrict-access-wordpress/\">restrict access to anything in WordPress</a> to get a full picture of all the things PMPro can protect directly, without the need for custom roles.</li>\n</ul>\n\n\n\n<p><strong>You do not need to keep using custom roles if:</strong></p>\n\n\n\n<ul>\n<li>Your members have basic WordPress user access (equivalent to the “Subscriber” role). This role can view content and edit their own user profile. PMPro can control whether Subscribers have access to a limited view of the WordPress admin area or are redirected from this unnecessary dashboard.</li>\n\n\n\n<li>Everything you need to protect can be done via <a href=\"https://www.paidmembershipspro.com/documentation/content-controls/\">PMPro’s extensive content protection settings</a>.</li>\n</ul>\n\n\n\n<h4 class=\"wp-block-heading\" id=\"set-up-pmpro-levels\">Set Up Membership Levels in Paid Memberships Pro</h4>\n\n\n\n<p>Whether you need to maintain custom roles or not, the next step is to set up your membership levels in PMPro.</p>\n\n\n\n<ul>\n<li>Navigate to Memberships &gt; Settings &gt; Membership Levels in the WordPress admin.</li>\n\n\n\n<li>Click Add New Level and configure level settings.</li>\n</ul>\n\n\n\n<p>Refer to our <a href=\"https://www.paidmembershipspro.com/documentation/initial-plugin-setup/step-1-add-new-membership-level/\">Initial Setup &#8211; Membership Levels documentation</a> or our general documentation on <a href=\"https://www.paidmembershipspro.com/documentation/membership-levels/initial-membership-level-setup/\">Membership Level Setup</a> for more help with this step.</p>\n\n\n\n<p>If you are preserving custom roles or otherwise need to adjust user capabilities for this level, check out the free Add On: <a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-roles/\">Roles for Membership Levels</a>. This Add On maps a default or custom WordPress user role to the membership level.</p>\n\n\n\n<p>This Add On is not required and should only be used for sites that must adjust user capabilities for their members.</p>\n\n\n\n<p>When a user signs up for this level, their role will be changed to the custom role you define. When they lose that membership level, their role is reset to the site default as defined under Settings &gt; General in the WordPress admin.</p>\n\n\n\n<p><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f449.png\" alt=\"👉\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> Make sure to save the membership level IDs for future reference, and hold off on connecting your payment gateway until later.</p>\n\n\n\n<h4 class=\"wp-block-heading\" id=\"staging-site-step\">If You’re Installing PMPro on a Staging Site</h4>\n\n\n\n<p>Once your staging site is set up, make sure that it’s not connected to WooCommerce via Ultimate Member’s WooCommerce Integration or your Paid Memberships Pro payment gateway.</p>\n\n\n\n<p>Note: During the migration process, some actions on your staging site might result in system emails being sent from WordPress, Ultimate Member, or Paid Memberships Pro. To prevent accidental sending of messages from your staging site, you can use the <a href=\"https://wordpress.org/plugins/disable-emails/\">Disable Emails plugin</a>.</p>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"set-up-pmpro-user-fields\">4. Set Up User Fields (If Applicable)</h3>\n\n\n\n<p>Ultimate Member is, primarily, a user registration and profile plugin. Paid Memberships Pro has all of these features—and more.</p>\n\n\n\n<p>Paid Memberships Pro utilizes <a href=\"https://www.paidmembershipspro.com/documentation/user-fields/\">User Fields</a> to create and gather additional checkout fields and custom profile fields for your members and administrators.</p>\n\n\n\n<p>If you aren’t using custom profile fields, proceed to <a href=\"#export-import\">Export &amp; Import Your Data</a>.</p>\n\n\n\n<h4 class=\"wp-block-heading\">User Fields</h4>\n\n\n\n<p>Ultimate Member fields are stored in user meta (just like the user fields created by Paid Memberships Pro). This is the WordPress database table that stores all the custom user data that is not part of the user object itself.</p>\n\n\n\n<p>Paid Memberships Pro features an intuitive settings interface to create and manage custom profile fields. You’ll need to exactly match the Meta Key name in Ultimate Member to the Field Name in PMPro.</p>\n\n\n\n<p>Once this is done, PMPro will automatically recognize the existing user meta information for all your members. You do not have to export and re-import that data.</p>\n\n\n\n<ol>\n<li>Navigate to Memberships &gt; Settings &gt; User Fields in the WordPress admin.</li>\n\n\n\n<li>Create a new field group or add fields to the default group.</li>\n\n\n\n<li>Add a new field for every custom field you want to maintain from your Ultimate Member forms.</li>\n\n\n\n<li>When you create fields, you must set the field’s name to the exact same Meta Key used in your Ultimate Member Forms.\n<ul>\n<li>Navigate to Ultimate Member &gt; Forms in the WordPress admin.</li>\n\n\n\n<li>Select a form to edit.</li>\n\n\n\n<li>Edit a field in the Form Builder section.</li>\n\n\n\n<li>Locate the field’s setting for Meta Key.</li>\n\n\n\n<li>This is the exact value you want to use for the Name field in PMPro User Fields settings.</li>\n</ul>\n</li>\n</ol>\n\n\n\n<figure class=\"wp-block-image size-full is-style-box-shadow\"><img decoding=\"async\" loading=\"lazy\" width=\"1200\" height=\"800\" src=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrate-Ultimate-Member-Field-to-PMPro-User-Field.jpg\" alt=\"Screenshot showing where to enter Ultimate Member Meta Key into PMPro User Fields\" class=\"wp-image-508762\" srcset=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrate-Ultimate-Member-Field-to-PMPro-User-Field.jpg 1200w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrate-Ultimate-Member-Field-to-PMPro-User-Field-300x200.jpg 300w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrate-Ultimate-Member-Field-to-PMPro-User-Field-1024x683.jpg 1024w, https://www.paidmembershipspro.com/wp-content/uploads/2023/07/Migrate-Ultimate-Member-Field-to-PMPro-User-Field-1170x780.jpg 1170w\" sizes=\"(max-width: 1200px) 100vw, 1200px\" /></figure>\n\n\n\n<ol start=\"5\">\n<li>Repeat these steps for each custom profile field you want to preserve.</li>\n\n\n\n<li>Add new fields for new data you want to capture.</li>\n\n\n\n<li>Save your fields settings.</li>\n</ol>\n\n\n\n<p>If you are trying to create a User Field for a field type that PMPro does not support via the Use Fields Settings page, it may be possible to set the field up via custom code.</p>\n\n\n\n<p>Check out the <a href=\"https://www.paidmembershipspro.com/documentation/user-fields/\">User Fields documentation</a> for specific help configuring fields.&nbsp;</p>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"export-import\">5. Export and Import Your Data</h3>\n\n\n\n<p><em>Before you start playing with CSV files, here’s a quick sidebar that makes migration easier for a majority of sites…</em></p>\n\n\n\n<p>Unlike our other migration guides, the Ultimate Member migration can be completed without an import. Here’s the case where this is possible:</p>\n\n\n\n<ul>\n<li>You are only using Ultimate Member to register users with the Subscriber role.</li>\n\n\n\n<li>You want all Ultimate Member members to get the same free membership level in Paid Memberships Pro.</li>\n</ul>\n\n\n\n<p>If this sounds like exactly what you need, this process is a whole lot simpler. Skip everything we have below about exporting data and preparing an import, yadda, yadda.</p>\n\n\n\n<p>Instead, check out this tutorial on <a href=\"https://www.paidmembershipspro.com/database-script-apply-membership-level-users-without-level/\">how to bulk apply a membership level to all users without a level</a>. This single database script can be run on your entire Users list to give every User the same level of membership access.</p>\n\n\n\n<p><a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-toolkit/\">Our PMPro Toolkit Add On</a> also includes a script under the Tools menu of your dashboard. This script gives existing non-member users a membership level without having to run SQL queries yourself.</p>\n\n\n\n<p>Then proceed to <a href=\"#resume-your-live-website\">Resume Your Live Website</a> below.</p>\n\n\n\n<p>If you are managing multiple Ultimate Member roles, keep following the steps below on how to export your Ultimate Member data.</p>\n\n\n\n<h4 class=\"wp-block-heading\" id=\"export-ultimate-member-data\">First, Export Your Ultimate Member Data</h4>\n\n\n\n<p>Ultimate Member does not offer any built-in Member Export features. Instead, you’ll need to use the <a href=\"https://wordpress.org/plugins/import-users-from-csv-with-meta/\">Import and export users and customers plugin</a> by codection to get your member data out of Ultimate Member.</p>\n\n\n\n<ol>\n<li>Navigate to Plugins &gt; Add New</li>\n\n\n\n<li>Search for <strong>Import and export users and customers</strong></li>\n\n\n\n<li>Install and Activate the plugin (you want the plugin by <em>codection</em>)</li>\n\n\n\n<li>Navigate to Tools &gt; <em>Import and export users and customers</em> in the WordPress admin</li>\n\n\n\n<li>Select the <strong>Export</strong> tab and configure the export settings for your needs. Here is our recommended export setup:\n<ol>\n<li><strong>Roles:</strong> All roles</li>\n\n\n\n<li><strong>Columns:</strong> user_email,username,role</li>\n\n\n\n<li>You do not need to modify any other export settings</li>\n</ol>\n</li>\n\n\n\n<li>Click Download to export your data to CSV</li>\n</ol>\n\n\n\n<p>Now that you have your Ultimate Member data, you are ready to create a file that PMPro can use to import members.</p>\n\n\n\n<p>There are two plugins you will need:</p>\n\n\n\n<ol>\n<li><a href=\"https://wordpress.org/plugins/import-users-from-csv/\">Import Users from CSV</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-import-users-csv/\">Import Users from CSV Integration</a></li>\n</ol>\n\n\n\n<p>We have a standard <a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-import-users-csv/#template\">Import Users CSV template</a> for PMPro that serves as a decent baseline.</p>\n\n\n\n<h4 class=\"wp-block-heading\" id=\"prepare-import\">Prepare Your Import File</h4>\n\n\n\n<p>The table below shows the Ultimate Member data fields you should map to the required PMPro Import fields:</p>\n\n\n\n<div class=\"wp-block-group alignfull has-grey-blue-background-color has-background has-global-padding is-layout-constrained wp-block-group-is-layout-constrained\">\n<figure class=\"wp-block-table alignwide is-style-regular\"><table class=\"has-white-background-color has-background has-fixed-layout\"><thead><tr><th><strong>PMPro Column</strong></th><th><strong>Exported Data Column</strong></th><th><strong>Data Description and Location</strong></th></tr></thead><tbody><tr><td>user_login</td><td>username</td><td>Required field to match the imported data to the existing WordPress user.</td></tr><tr><td>role</td><td>role</td><td>When you switch from Ultimate Member to Paid Memberships Pro, most sites will want to change all member roles to the WordPress site default role of &#8220;subscriber&#8221;.<br><br>This is the base level role and capability for users in WordPress.<br><br>Before you edit this column, duplicate the data so that you can use the Ultimate Member role names to match to your new PMPro Membership Level IDs.</td></tr><tr><td>membership_id</td><td>(add this)</td><td>Add the numerical ID for the level in Paid Memberships Pro. Match the former Ultimate Member role name to the corresponding (new) PMPro Membership Level ID.</td></tr><tr><td>membership_status</td><td>N/A</td><td>For this migration, it is recommended that you only import members with an ‘active’ status and remove all others. Create a role for <code>membership_status</code> with the value <code>active</code></td></tr></tbody></table></figure>\n</div>\n\n\n\n<p>There are more optional fields you can add to your import spreadsheet, such as membership start and end dates (if you want to set an expiration date for your members). Ultimate Member doesn’t natively support expiration dates, but they do integrate with WooCommerce in their Pro version. If you were using that integration and want to set an end date for members based on when their membership should renew, you can include that here.</p>\n\n\n\n<p>For more guidance on maintaining active subscriptions or setting additional fields in your import file, refer to the Import Users From CSV Integration documentation.</p>\n\n\n\n<h4 class=\"wp-block-heading\">Export the file to CSV and Test the Import</h4>\n\n\n\n<p>We recommend testing the import with a subset of your data (the first 10 records) to ensure that everything works as expected.</p>\n\n\n\n<ol>\n<li>First, export the first few rows CSV file With your exported .csv file, adjust the rows to only include the first 5 or 10 records.</li>\n\n\n\n<li>Run that import by following the directions on the <a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-import-users-csv/#settings\">Import Users from CSV Add On </a>documentation page.</li>\n\n\n\n<li>Double check the data to be sure everything looks correct. You can check your Members List under Memberships &gt; Members.</li>\n</ol>\n\n\n\n<p>If everything looks correct, you are ready to do the full and final import. To do this, repeat the process from your test above using the full set of data (minus those test members already imported).</p>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"resume-your-live-website\">6. Resume Your Live Website</h3>\n\n\n\n<p>You’re almost done! Now it’s time to connect your payment gateway in PMPro.</p>\n\n\n\n<p>According to the Ultimate Member docs, you can completely remove Ultimate Member from your WordPress by following these steps:</p>\n\n\n\n<ol>\n<li>Navigate to Ultimate Member &gt; Settings &gt; Misc in the WordPress admin.</li>\n\n\n\n<li>Enable the <strong>Remove Data on Uninstall</strong> setting, then save changes.\n<ol>\n<li>When this setting is turned on the Ultimate Member plugin will completely remove all data when the plugin/extension is deleted.</li>\n</ol>\n</li>\n\n\n\n<li>Navigate to <strong>Plugins</strong> in the WordPress admin</li>\n\n\n\n<li><strong>Deactivate</strong> the Ultimate Member plugin</li>\n\n\n\n<li><strong>Delete</strong> the Ultimate Member plugin</li>\n</ol>\n\n\n\n<p>This process only removes Ultimate Member plugin settings. It does not change any user information, including their current role or profile fields.</p>\n\n\n\n<p>Now, activate your payment gateway in Paid Memberships Pro. PMPro has only one settings page to configure the payment gateway for all memberships. Just navigate to Memberships &gt; Settings &gt; Payment Settings. The process of <a href=\"https://www.paidmembershipspro.com/documentation/initial-plugin-setup/step-3-payment-gateway-security/\">setting up a payment gateway</a> is also outlined in the Initial Setup guide we mentioned earlier.</p>\n\n\n\n<h2 class=\"wp-block-heading\" id=\"configure-content-protection-and-features\">Part 2: Configuring Content Protection and Other Features</h2>\n\n\n\n<p>There are some features in Ultimate Member that are handled a little differently with PMPro. In this section, we cover the main content protection features of PMPro as well as how to closely replicate other features you may have been using in Ultimate Member</p>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"restrict-wordpress-content\">Restrict Access to WordPress Content</h3>\n\n\n\n<p>Every membership plugin handles content restrictions in a different way. With Paid Memberships Pro, you can restrict individual pages, posts, or entire categories of posts.</p>\n\n\n\n<p>Browse our <a href=\"https://www.paidmembershipspro.com/restrict-access-wordpress/\">documentation on restricting content</a> to learn more about the various ways you can protect your content with PMPro.</p>\n\n\n\n<p>This includes:</p>\n\n\n\n<ul>\n<li><a href=\"https://www.paidmembershipspro.com/documentation/content-controls/for-pages/\">Pages</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/documentation/content-controls/for-posts/\">Posts and Categories</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/custom-post-type-membership-access/\">Other content types including CPTs</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-courses-lms-integration/\">Courses</a></li>\n\n\n\n<li>Community features like <a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-bbpress/\">bbPress</a> and <a href=\"https://www.paidmembershipspro.com/add-ons/buddypress-integration/\">BuddyPress</a></li>\n</ul>\n\n\n\n<p>You’ll need to manually update every piece of content that was protected through the Ultimate Member methods to now use the Paid Memberships Pro methods. If you have a very large membership site, there are some developer-level methods to bulk apply content restrictions.&nbsp;</p>\n\n\n\n<p>Here are a two primary resources:</p>\n\n\n\n<ul>\n<li><a href=\"https://www.paidmembershipspro.com/restrict-access-bulk-methods/\">Methods to Restrict Access by Membership Level in Bulk</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-toolkit/\">Developer’s Toolkit</a>: Add On to duplicate content restrictions for newly added levels.</li>\n</ul>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"create-member-directory\">Create a Member Directory and Public or Private Profiles</h3>\n\n\n\n<p>Paid Memberships Pro offers a plugin for <a href=\"https://www.paidmembershipspro.com/add-ons/member-directory/\">Member Directory and Profiles</a> which you can check out here.</p>\n\n\n\n<p>This plugin is necessary for sites that want to maintain Ultimate Member’s directory and profile features.</p>\n\n\n\n<p>You can customize the appearance and layout of the member directory using shortcode and block settings. The member directory and profile page can be adjusted to match your necessary fields, layout, and privacy settings.</p>\n\n\n\n<p>When you have your users imported into PMPro and have the PMPro Member Directory Add On installed and activated, users should all auto-show in the default PMPro member directory.</p>\n\n\n\n<p>While Paid Memberships Pro provides a basic member directory functionality, you may need to develop more advanced member filtering and search options on your own. We have a basic guide on <a href=\"https://www.paidmembershipspro.com/allow-users-to-filter-results-in-your-member-directory/\">how to allow members to filter results in your Member Directory</a> that you can use to guide this extended development.</p>\n\n\n\n<h3 class=\"wp-block-heading\" id=\"replicate-features\">Replicate Other Ultimate Member Features</h3>\n\n\n\n<p>To replicate the social features of Ultimate Member with Paid Memberships Pro, you may need to consider adding other plugins such as BuddyPress or bbPress.</p>\n\n\n\n<p>Not every membership site is using these features (I’m talking about things like messaging, shared profiles, directories, and member search). If you are, here are a few additional links to explore:</p>\n\n\n\n<ul>\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/buddypress-integration/\">BuddyPress Integration&nbsp;</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-bbpress/\">bbPress Integration</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/member-homepages/\">Member Homepages</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-nav-menus/\">Nav Menus Add On</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/redirect-non-members-away-from-member-content/\">Redirect Non-Members Away From Member Content</a></li>\n</ul>\n\n\n\n<p>Ultimate Member also has some features related to redirection, homepage access, and more. Refer to these additional resources if you were using these features.</p>\n\n\n\n<ul>\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/member-directory/\">Member Directory and Profile Pages</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/member-homepages/\">Member Homepages</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/add-ons/pmpro-nav-menus/\">Nav Menus Add On</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/redirect-non-members-away-from-member-content/\">Redirect Non-Members Away From Member Content</a></li>\n</ul>\n\n\n\n<h2 class=\"wp-block-heading\" id=\"migration-complete\">Welcome to The Future: A More Powerful Membership Site</h2>\n\n\n\n<p>You did it! You’ve successfully migrated your members from Ultimate Member to Paid Memberships Pro, and configured your content protection so that your membership site is officially ready for action.</p>\n\n\n\n<p>You can now think about how you want to grow your membership—perhaps through some of PMPro’s powerful revenue-generating features like:</p>\n\n\n\n<ul>\n<li>Recurring subscriptions made possible through our free built-in <a href=\"https://www.paidmembershipspro.com/gateway/\">payment gateways</a>.</li>\n\n\n\n<li>New membership levels with <a href=\"https://www.paidmembershipspro.com/restrict-access-wordpress/\">exclusive premium content</a>.</li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/courses/\">Members-only Courses</a></li>\n\n\n\n<li><a href=\"https://www.paidmembershipspro.com/communities/\">Community Features</a></li>\n</ul>\n\n\n\n<p>… and loads more.</p>\n\n\n\n<p>If you’re still in need of additional help, be sure to check the Frequently Asked Questions section below, or contact our <a href=\"https://www.paidmembershipspro.com/support/\">support</a> team if you’re a premium member.</p>\n\n\n\n<p>We’re excited that you’ve made the switch to the best membership plugin for WordPress, and we’re right here by your side to help you take your membership business to new heights. <img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f680.png\" alt=\"🚀\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<h2 class=\"wp-block-heading\" id=\"frequently-asked-questions\">Frequently Asked Questions</h2>\n\n\n<div class=\"memberlite_accordion \"><div id=\"memberlite_accordion-item_1\" class=\"memberlite_accordion-item \"><h3>This seems really complicated!</h3><div class=\"memberlite_accordion-item-content\">\n\n\n\n<p>If this is all too overwhelming or confusing for you, you should consider hiring a developer. We recommend working with one of our <a href=\"/developers/\">Codeable freelancers or agency partners</a> to make sure your migration goes as smoothly as possible.</p>\n\n\n\n<p>If you’re a premium member, you can always reach out to our <a href=\"/support/\">support team</a> for help as well.</p>\n\n\n<p></p></div></div><div id=\"memberlite_accordion-item_2\" class=\"memberlite_accordion-item \"><h3>What is a Staging Site?</h3><div class=\"memberlite_accordion-item-content\">\n\n\n\n<p>A staging site is essentially a duplicate of your website, where you can test out new features and make changes <em>without</em> affecting your live website.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-style-box-shadow\"><img decoding=\"async\" loading=\"lazy\" width=\"1024\" height=\"576\" src=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/06/original-site-staging-1024x576.jpg\" alt=\"Original Site vs Staging Site Infographic\" class=\"wp-image-505056\" srcset=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/06/original-site-staging-1024x576.jpg 1024w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/original-site-staging-300x169.jpg 300w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/original-site-staging-1536x864.jpg 1536w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/original-site-staging-1170x658.jpg 1170w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/original-site-staging-128x72.jpg 128w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/original-site-staging.jpg 1600w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" /></figure>\n\n\n\n<p>Creating a staging site allows you to get all of your membership data in place <em>before</em> merging it with your live site.</p>\n\n\n\n<div class=\"wp-block-group content-callout has-background is-layout-flow wp-block-group-is-layout-flow\" style=\"background-color:#f5f8fa\">\n<div class=\"wp-block-group is-nowrap is-layout-flex wp-container-129 wp-block-group-is-layout-flex\">\n<p><i class=\"fa  fa-glasses\"></i> <strong>Related:</strong> <a href=\"https://www.paidmembershipspro.com/why-you-should-use-a-staging-environment/\">Why You Should Use a Staging Environment</a></p>\n</div>\n</div>\n\n\n<p></p></div></div><div id=\"memberlite_accordion-item_3\" class=\"memberlite_accordion-item \"><h3>Do I Need a Staging Site?</h3><div class=\"memberlite_accordion-item-content\">\n\n\n\n<p>A staging site is a non-public version of your site’s code that powers your live membership site. Your membership’s staging site, if set up properly, will allow you to add and test all kinds of things before releasing them on your live site.</p>\n\n\n\n<div class=\"wp-block-group content-callout has-background is-layout-flow wp-block-group-is-layout-flow\" style=\"background-color:#f5f8fa\">\n<div class=\"wp-block-group is-nowrap is-layout-flex wp-container-131 wp-block-group-is-layout-flex\">\n<p><i class=\"fa  fa-glasses\"></i> <strong>Related:</strong> <a href=\"https://www.paidmembershipspro.com/why-you-should-use-a-staging-environment/\">How to Set Up a Staging Environment</a></p>\n</div>\n</div>\n\n\n\n<p>With a staging site, you can test things like:</p>\n\n\n\n<ul>\n<li>Code recipes</li>\n\n\n\n<li>New features</li>\n\n\n\n<li>New plugins</li>\n\n\n\n<li>Plugin updates</li>\n</ul>\n\n\n\n<p>If you have a <strong>very active membership site</strong> that receives a lot of traffic, or you don’t have a lot of development experience and <strong>you want to experiment without killing your live site</strong>, using a staging site is a wise choice.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-style-box-shadow\"><img decoding=\"async\" loading=\"lazy\" width=\"1024\" height=\"576\" src=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/06/do-you-need-a-staging-site-1024x576.jpg\" alt=\"Do you need a staging site infographic\" class=\"wp-image-505057\" srcset=\"https://www.paidmembershipspro.com/wp-content/uploads/2023/06/do-you-need-a-staging-site-1024x576.jpg 1024w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/do-you-need-a-staging-site-300x169.jpg 300w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/do-you-need-a-staging-site-1536x864.jpg 1536w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/do-you-need-a-staging-site-1170x658.jpg 1170w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/do-you-need-a-staging-site-128x72.jpg 128w, https://www.paidmembershipspro.com/wp-content/uploads/2023/06/do-you-need-a-staging-site.jpg 1600w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" /></figure>\n\n\n<p></p></div></div></div>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}s:27:\"http://www.w3.org/2005/Atom\";a:1:{s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:4:\"href\";s:40:\"https://www.paidmembershipspro.com/feed/\";s:3:\"rel\";s:4:\"self\";s:4:\"type\";s:19:\"application/rss+xml\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:44:\"http://purl.org/rss/1.0/modules/syndication/\";a:2:{s:12:\"updatePeriod\";a:1:{i:0;a:5:{s:4:\"data\";s:9:\"\n	hourly	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:15:\"updateFrequency\";a:1:{i:0;a:5:{s:4:\"data\";s:4:\"\n	1	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}}}}}}s:4:\"type\";i:128;s:7:\"headers\";O:48:\"WpOrg\\Requests\\Utility\\CaseInsensitiveDictionary\":1:{s:7:\"\0*\0data\";a:9:{s:6:\"server\";s:17:\"Sucuri/Cloudproxy\";s:4:\"date\";s:29:\"Fri, 08 Sep 2023 10:13:23 GMT\";s:12:\"content-type\";s:34:\"application/rss+xml; charset=UTF-8\";s:11:\"x-sucuri-id\";s:5:\"13003\";s:13:\"last-modified\";s:29:\"Fri, 08 Sep 2023 09:14:57 GMT\";s:16:\"content-encoding\";s:4:\"gzip\";s:4:\"vary\";s:15:\"Accept-Encoding\";s:15:\"referrer-policy\";s:26:\"no-referrer-when-downgrade\";s:14:\"x-sucuri-cache\";s:3:\"HIT\";}}s:5:\"build\";s:14:\"20211220193300\";}','no'),(381,'_transient_timeout_feed_mod_5966b7b1b1ba5243c38216abce94203f','1694211203','no'),(382,'_transient_feed_mod_5966b7b1b1ba5243c38216abce94203f','1694168003','no'),(383,'pmpro_visits','a:9:{s:5:\"today\";i:19;s:8:\"thisdate\";s:10:\"2023-08-09\";s:4:\"week\";i:19;s:8:\"thisweek\";s:2:\"36\";s:5:\"month\";i:19;s:9:\"thismonth\";